import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm'
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm'
function App() {
    const [currentIndex, setCurrentIndex] = React.useState(0);

    const [key, setKey] = React.useState(0);

    const handleNext = () => {
        const index = (currentIndex + 1) % appsmith.model.tips.length
        setCurrentIndex(index);
        appsmith.updateModel({
            currentIndex: index
        });
    };

    const handleReset = () => {
        setCurrentIndex(0);
        appsmith.updateModel({
            currentIndex: 0
        });
        appsmith.triggerEvent("onResetClick", {
            oldIndex: currentIndex
        });
    };

    React.useEffect(() => {        
        appsmith.onModelChange((model, prevModel) => {
            if (JSON.stringify(prevModel?.tips) !== JSON.stringify(model.tips)) {
                setKey(Math.random());
            }
        });
    }, []);


    return (
        <div className="app" key={key}>
            <div className="tip-container">
                <div className="tip-header">
                    <h2>Custom Widget</h2>
                    <div>{currentIndex + 1} / {appsmith.model.tips.length}        </div>
                </div>
                <div>{appsmith.model.tips[currentIndex]}</div>
            </div>
            <div className="button-container">
                <button className="primary" onClick={handleNext} type="primary">Next Tip</button>
                <button className="reset" onClick={handleReset}>Reset</button>
            </div>
    </div>
);
}

appsmith.onReady(() => {
    reactDom.render(<App />, document.getElementById("root"));
});
