import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import ReactDOM from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';
function TableWidget() {
    // Mock data for the table
    const [data, setData] = React.useState(appsmith.model.data || [
        { name: 'John Doe', age: 28, occupation: 'Engineer' },
        { name: 'Jane Smith', age: 34, occupation: 'Designer' },
        { name: 'Sam Green', age: 45, occupation: 'Manager' }
    ]);
    React.useEffect(() => {
        appsmith.onModelChange((model, prevModel) => {
            if (JSON.stringify(model.data) !== JSON.stringify(prevModel?.data)) {
                setData(model.data);
            }
        });
    }, []);
    return (
        <div className="table-container">
            <table className="table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Age</th>
                        <th>Occupation</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, index) => (
                        <tr key={index}>
                            <td>{row.name}</td>
                            <td>{row.age}</td>
                            <td>{row.occupation}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}
appsmith.onReady(() => {
    ReactDOM.render(<TableWidget />, document.getElementById("root"));
});

